INSERT INTO paylater.tbl_tnc_explain (description,id_tnc,created_date,updated_date,id_tnc_title,id_tnc_subtitle) VALUES
	 ('Seluruh instruksi perbankan dari Nasabah (“Anda”) akan disampaikan dari aplikasi e-Form website NOBU KTA atau dilakukan secara online.',1,'2022-05-12 15:34:08','2022-05-12 15:34:08',1,NULL),
	 ('Dengan mengakses dan mengisi semua isian, serta melengkapi semua persyaratan dalam website e-Form NOBU KTA, maka Anda dengan ini telah melakukan pengajuan pinjaman NOBU KTA kepada Bank atas nama Anda melalui e-Form website Nobu KTA yang dioperasikan dan/atau dikelola oleh Bank.',1,'2022-05-12 15:36:18','2022-05-12 15:36:18',1,NULL),
	 ('Dengan memberikan persetujuan elektronik dalam website e-Form NOBU KTA, maka Anda telah mengetahui, membaca, memahami dan menyetujui untuk melaksanakan ketentuan yang ada dalam NOBU KTA, termasuk namun tidak terbatas pada melaksanakan setiap instruksi dan persyaratan yang ditentukan.',1,'2022-05-12 15:36:18','2022-05-12 15:36:18',1,NULL),
	 ('Anda akan diminta untuk melakukan otorisasi instruksi untuk keperluan verifikasi pinjaman dengan menggunakan berbagai jenis informasi keamanan yang berbeda [(misalnya : One Time Password (OTP)] sesuai dengan permintaan.',1,'2022-05-12 15:36:18','2022-05-12 15:36:18',1,NULL),
	 ('Setelah melakukan otorisasi, Bank akan menjalankan instruksi sesuai perintah Anda, dan Anda wajib memastikan tidak memberitahukan informasi keamanan Anda kepada orang lain, baik secara langsung maupun tidak langsung.',1,'2022-05-12 15:36:18','2022-05-12 15:36:18',1,NULL),
	 ('Sesuai dengan persyaratan Otoritas Jasa Keuangan (OJK), Anda diminta untuk memberikan data pribadi Anda saat melakukan pengajuan pinjaman baik melalui website NOBU KTA, Tenaga Pemasar Bank ataupun melalui Customer Service di Kantor Bank.',1,'2022-05-12 15:36:18','2022-05-12 15:36:18',1,NULL),
	 ('Anda bertanggung jawab untuk memastikan informasi yang Anda berikan adalah informasi yang benar, akurat dan terkini. Anda berkewajiban untuk menginformasikan kembali kepada Bank jika terdapat perubahan berdasarkan data perubahan yang legal dan valid.',1,'2022-05-12 15:36:18','2022-05-12 15:36:18',1,NULL),
	 ('Bank memastikan kerahasiaan dan keamanan informasi yang Anda berikan terjaga dengan baik. Penggunaan data Anda oleh Bank dilakukan sesuai dengan ketentuan yang berlaku.',1,'2022-05-12 15:36:18','2022-05-12 15:36:18',1,NULL),
	 ('Bank akan selalu bersikap transparan mengenai produk / layanan Bank, termasuk ketika terdapat perubahan dalam Syarat & Ketentuan sesuai dengan ketentuan yang berlaku.',1,'2022-05-12 15:36:18','2022-05-12 15:36:18',1,NULL),
	 ('Bank memiliki tabel angsuran yang dapat Anda lihat di website. Bank telah membuat daftar biaya secara ringkas untuk menghindari kebingungan.',1,'2022-05-12 15:36:18','2022-05-12 15:36:18',1,NULL);
INSERT INTO paylater.tbl_tnc_explain (description,id_tnc,created_date,updated_date,id_tnc_title,id_tnc_subtitle) VALUES
	 ('Segala pertanyaan mengenai NOBU KTA dapat Anda tanyakan setiap saat dengan menghubungi Nobu Call di layanan nomor 1 500 278 selama 24 jam 7 hari dalam seminggu.',1,'2022-05-12 15:36:18','2022-05-12 15:36:18',1,NULL),
	 ('Bank” atau dapat juga ditulis “Kreditor” adalah PT Bank Nationalnobu Tbk, yang memiliki jaringan atau seluruh kantor pelayanan, yang terdiri dari kantor pusat, kantor cabang, kantor cabang pembantu atau kantor kas.',2,'2022-05-12 15:41:10','2022-05-12 15:41:10',2,NULL),
	 ('“NOBU KTA” adalah produk pinjaman yang ditawarkan oleh PT Bank Nationalnobu Tbk (“Bank”), dimana Anda tidak perlu memberikan jaminan / agunan,  yang pembayarannya dapat dicicil bulanan dengan jangka waktu yang lebih panjang.',2,'2022-05-12 15:45:19','2022-05-12 15:45:19',2,NULL),
	 ('“Debitor” adalah individu / orang perorangan yang memperoleh Fasilitas Kredit dari Bank sebagaimana yang tercantum dalam Perjanjian Kredit.',2,'2022-05-12 15:45:19','2022-05-12 15:45:19',2,NULL),
	 ('“Whitelist User” adalah istilah atau nama pengguna NOBU KTA yang mendapatkan undangan atau penawaran khusus program NOBU KTA dari Bank melalui saluran komunikasi resmi milik Bank.',2,'2022-05-12 15:45:19','2022-05-12 15:45:19',2,NULL),
	 ('“Fasilitas Kredit” adalah fasilitas pinjaman uang berupa pinjaman dengan angsuran yang besaran jumlah dan waktunya ditentukan oleh Bank yang menimbulkan kewajiban membayar bagi Debitor kepada Bank berdasarkan Perjanjian Kredit.',2,'2022-05-12 15:45:19','2022-05-12 15:45:19',2,NULL),
	 ('“Utang” adalah semua jumlah uang yang terutang dan wajib dibayar oleh Debitor pada suatu waktu kepada Kreditor berdasarkan Perjanjian Kredit, yang terdiri dari Utang Pokok (yaitu jumlah kredit yang telah ditarik oleh Debitor), bunga, biaya-biaya, denda dan kewajiban pembayaran lain dari Debitor.',2,'2022-05-12 15:45:19','2022-05-12 15:45:19',2,NULL),
	 ('“Perjanjian Kredit” adalah Perjanjian yang dibuat oleh dan antara Bank dan Debitor yang mengatur lebih lanjut tentang syarat dan ketentuan Fasilitas Kredit.',2,'2022-05-12 15:45:19','2022-05-12 15:45:19',2,NULL),
	 ('“e-Form” adalah formulir elektronik yang digunakan sebagai sarana input data Calon Debitor yang melakukan pengajuan pinjaman melalui website secara online.',2,'2022-05-12 15:45:19','2022-05-12 15:45:19',2,NULL),
	 ('“Call Center / Nobu Call” adalah unit pada Bank yang disediakan untuk  menyediakan layanan Nasabah yang dapat dihubungi lewat panggilan telepon, e-mail, dan/atau chat atau media lainnya sesuai ketentuan Bank dan media yang tersedia.',2,'2022-05-12 15:45:19','2022-05-12 15:45:19',2,NULL);
INSERT INTO paylater.tbl_tnc_explain (description,id_tnc,created_date,updated_date,id_tnc_title,id_tnc_subtitle) VALUES
	 ('“Peralatan” berarti semua alat elektronik, wireless, komunikasi, transmisi atau peralatan telekomunikasi, perangkat atau media yang termasuk dan tidak terbatas pada internet, komputer atau peralatan mobile, perangkat, terminal atau sistem yang mungkin dibutuhkan untuk mengakses dan menggunakan website Nobu KTA.',2,'2022-05-12 15:45:19','2022-05-12 15:45:19',2,NULL),
	 ('“Data” berarti adalah setiap data, informasi dan/atau keterangan dalam bentuk apapun yang dari waktu ke waktu Anda sampaikan kepada Bank baik melalui website pengajuan pinjaman (e-form) termasuk tetapi tidak terbatas pada :
INSERT INTO paylater.tbl_tnc_explain (description,id_tnc,created_date,updated_date,id_tnc_title,id_tnc_subtitle) VALUES
	 ('Pengajuan pinjaman dilakukan melalui e-Form Nobu KTA dan seluruh proses akan diselesaikan secara online. Dengan demikian, Anda sebagai (calon) Debitor harus memiliki smartphone dan memiliki nomor ponsel Indonesia dan e-mail yang aktif dan valid agar dapat menerima notifikasi atau OTP melalui SMS dan/atau e-mail yang akan dikirim oleh Bank.',2,'2022-05-12 15:49:15','2022-05-12 15:49:15',3,NULL),
	 ('Selama proses pengajuan Pinjaman NOBU KTA Anda dilarang untuk mengganti nomor telepon selular Anda hingga proses pencairan NOBU KTA dinyatakan berhasil atau gagal diproses. Hal ini terkait dengan adanya proses otentikasi/verifikasi oleh Bank atas nomor telepon yang Anda gunakan adalah sesuai dengan data yang disampaikan kepada Bank. ',2,'2022-05-12 15:49:15','2022-05-12 15:49:15',3,NULL),
	 ('Sebagai prasyarat, Anda harus memberikan nomor ponsel dan alamat e-mail aktif milik sendiri yang memiliki kapasitas memadai untuk menerima notifikasi atau OTP yang dikirim oleh Bank. Bank tidak bertanggung jawab atas keabsahan, kepemilikan, aktivitas dan kapasitas alamat e-mail tersebut.',2,'2022-05-12 15:49:15','2022-05-12 15:49:15',3,NULL),
	 ('Pada saat pengajuan pinjaman NOBU KTA Anda disetujui/ditolak oleh Bank, Bank akan mengirimkan notifikasi melalui telepon atau e-mail atau SMS atau media komunikasi formal bank lainnya bahwa pengajuan pinjaman Anda telah disetujui/ditolak.',2,'2022-05-12 15:49:15','2022-05-12 15:49:15',3,NULL),
	 ('Debitor setuju untuk melepaskan Bank dari seluruh kerugian, tanggung jawab, tuntutan dan biaya (termasuk biaya adanya gugatan hukum) yang dapat muncul terkait dengan eksekusi instruksi Nasabah, kecuali Debitor dapat membuktikan lain dan/atau transaksi tersebut dieksekusi karena kesalahan Bank sesuai ketentuan perundang- undangan yang berlaku.',2,'2022-05-12 15:49:15','2022-05-12 15:49:15',3,NULL),
	 ('Menyimpang dari ketentuan-ketentuan tersebut di atas, Fasilitas Kredit bersifat Uncommitted sehingga atas Fasilitas Kredit yang belum dilakukan penarikan, Bank tetap mempunyai hak untuk menunda atau mengatur kembali atau mengurangi dan membatalkan Fasilitas Kredit.',2,'2022-05-12 15:49:15','2022-05-12 15:49:15',3,NULL),
	 ('Fasilitas kredit diberikan berdasarkan form permohonan pinjaman dan mengacu kepada syarat dan ketentuan dalam Perjanjian Kredit.',2,'2022-05-12 15:49:15','2022-05-12 15:49:15',3,NULL),
	 ('Untuk Fasilitas Kredit yang cara pembayarannya dengan angsuran, penarikan Fasilitas Kredit yang diberikan Bank akan dicairkan secara sekaligus dengan syarat dan ketentuan yang tercantum di dalam Perjanjian Kredit.',2,'2022-05-12 15:49:15','2022-05-12 15:49:15',3,NULL),
	 ('Perjanjian Kredit berlaku untuk selama jangka waktu yang disepakati antara Bank dengan Nasabah dan dicantumkan pada Fasilitas Kredit.',2,'2022-05-12 15:49:15','2022-05-12 15:49:15',3,NULL),
	 ('Pembayaran Fasilitas Kredit berupa bunga, pokok, angsuran (pokok dan bunga) dan atau jumlah lainnya yang terutang dan wajib dibayarkan kepada Bank dilakukan sesuai dengan ketentuan pembayaran bunga, pokok, angsuran (pokok dan bunga) dan/atau jumlah lainnya yang terutang.',2,'2022-05-12 15:49:15','2022-05-12 15:49:15',3,NULL);
INSERT INTO paylater.tbl_tnc_explain (description,id_tnc,created_date,updated_date,id_tnc_title,id_tnc_subtitle) VALUES
	 ('Bank berhak secara seketika mengakhiri Perjanjian Kredit dan/atau menolak pencairan kredit selanjutnya serta menuntut pembayaran dengan seketika dan sekaligus lunas atas utang antara lain apabila Debitor tidak membayar angsuran pada waktunya.',2,'2022-05-12 15:49:15','2022-05-12 15:49:15',3,NULL),
	 ('Biaya – biaya yang dikenakan ke Debitor:
INSERT INTO paylater.tbl_tnc_explain (description,id_tnc,created_date,updated_date,id_tnc_title,id_tnc_subtitle) VALUES
	 ('Bank memiliki hak mutlak untuk memperbaharui, memodifikasi atau mengubah situs web atau perangkat lunak apapun yang digunakan untuk mengakses NOBU KTA sewaktu-waktu tanpa pemberitahuan dan tanpa memberikan alasan apapun.',2,'2022-05-12 15:52:21','2022-05-12 15:52:21',4,2),
	 ('Apabia Debitor tidak membayar cicilan tepat waktu, maka akan mempengaruhi kualitas kredit dari Debitor yang akan tercatat dalam Sistem Layanan Informasi Keuangan (SLIK) OJK.',2,'2022-05-12 15:52:21','2022-05-12 15:52:21',4,2),
	 ('Bank berhak secara seketika mengakhiri Perjanjian Kredit dan/atau menolak pencairan kredit selanjutnya serta menuntut pembayaran dengan seketika dan sekaligus lunas atas utang antara lain apabila Debitor tidak membayar angsuran pada waktunya',2,'2022-05-12 15:52:21','2022-05-12 15:52:21',4,2),
	 ('Informasi keamanan berupa One Time Password (OTP) yang dibutuhkan untuk verifikasi yang menurut Bank dibutuhkan sebagai tujuan otentikasi, Debitor/ Calon Debitor harus memastikan untuk memasukan OTP yang sesuai.',2,'2022-05-12 15:54:47','2022-05-12 15:54:47',5,NULL),
	 ('Debitor/ Calon Debitor bertanggung jawab penuh dengan keamanan data terhadap seluruh penggunaan website NOBU KTA. Segala penggunaan dan/atau akibat penyelahgunaan yang dapat merugikan Nasabah baik yang dilakukan oleh Debitor/ Calon Debitor atau pihak lain sepenuhnya merupakan tanggung jawab Nasabah dan dengan demikian membebaskan Bank dari hal tersebut.',2,'2022-05-12 15:54:47','2022-05-12 15:54:47',5,NULL),
	 ('Debitor/ Calon Debitor menyetujui untuk membebaskan Bank dari segala kerugian, tanggung jawab, klaim dan biaya (termasuk biaya hukum) yang mungkin terjadi dalam kaitannya dengan pelaksanaan instruksi dari Bank, kecuali Debitor/ Calon Debitor dapat membuktikan sebaliknya dan/atau transaksi yang dijalankan karena kesalahan dari Bank sesuai ketentuan hukum yang berlaku.',2,'2022-05-12 15:54:47','2022-05-12 15:54:47',5,NULL),
	 ('Nasabah bertanggung jawab untuk memperoleh dan menggunakan perangkat lunak yang diperlukan dan/atau peralatan yang diperlukan untuk dapat mengakses website NOBU KTA dengan risiko yang ditanggung Nasabah.',2,'2022-05-12 15:54:47','2022-05-12 15:54:47',5,NULL),
	 ('Debitor/ Calon Debitor juga bertanggung jawab atas kinerja dan keamanan (termasuk tanpa batasan mengambil semua langkah yang diperlukan sejauh mungkin untuk mencegah penggunaan atau akses yang tidak sah) dari setiap peralatan yang digunakan oleh Debitor/ Calon Debitor untuk mengakses website NOBU KTA.',2,'2022-05-12 15:54:47','2022-05-12 15:54:47',5,NULL),
	 ('Nasabah harus memastikan bahwa peralatan yang digunakan untuk mengakses website NOBU KTA bebas dari kegagalan elektronik, mekanik, data yang gagal atau terkorupsi, virus komputer, bug dan/atau perangkat lunak yang berbahaya dan/atau perangkat lunak yang tidak diizinkan oleh penyedia layanan telekomunikasi, atau produsen atau vendor dari peralatan yang relevan.',2,'2022-05-12 15:54:47','2022-05-12 15:54:47',5,NULL),
	 ('Debitor/ Calon Debitor menyetujui bahwa Bank tidak bertanggung jawab atas kegagalan elektronik maupun mekanik, atau data yang terkorupsi, virus komputer, bug dan/atau perangkat lunak yang berbahaya lainnya dari jenis apapun yang mungkin timbul dari layanan yang disediakan oleh penyedia layanan internet yang relevan atau informasi penyedia layanan.',2,'2022-05-12 15:54:47','2022-05-12 15:54:47',5,NULL);
INSERT INTO paylater.tbl_tnc_explain (description,id_tnc,created_date,updated_date,id_tnc_title,id_tnc_subtitle) VALUES
	 ('Dengan melaksanakan transaksi melalui e-form NOBU KTA, Debitor/ Calon Debitor memahami bahwa seluruh komunikasi dan instruksi dari Debitor/ Calon Debitor yang diterima oleh Bank akan diperlakukan sebagai bukti solid meskipun tidak dibuat dalam bentuk dokumen tertulis atau diterbitkan dalam bentuk dokumen yang ditandatangani, dengan demikian Debitor/ Calon Debitor setuju untuk mengganti rugi dan melepaskan Bank dari segala kerugian, tanggung jawab, tuntutan dan pengeluaran (termasuk biaya litigasi) yang dapat muncul terkait dengan eksekusi dari instruksi Debitor/ Calon Debitor.',2,'2022-05-12 15:54:47','2022-05-12 15:54:47',5,NULL),
	 ('Debitor/ Calon Debitor menyatakan dan menjamin kepada Bank bahwa:',2,'2022-05-12 15:57:30','2022-05-12 15:57:30',6,NULL),
	 ('Tidak tersangkut dalam suatu perkara atau sengketa apapun juga',2,'2022-05-12 15:57:30','2022-05-12 15:57:30',6,NULL),
	 ('Tidak berada dalam keadaan pailit',2,'2022-05-12 15:57:30','2022-05-12 15:57:30',6,NULL),
	 ('Tidak berada dalam keadaan wanprestasi atau dinyatakan wanprestasi oleh pihak ketiga lainnya',2,'2022-05-12 15:57:30','2022-05-12 15:57:30',6,NULL),
	 ('Seluruh pinjaman atau utang Debitor/ Calon Debitor pada kreditur lainnya adalah lancar (kolektibilitas 1 (satu) sesuai ketentuan Bank Indonesia dan/atau Otoritas Jasa Keuangan yang berlaku); dan menjamin bahwa seluruh pernyataan dan informasi yang diberikan kepada Bank adalah benar.',2,'2022-05-12 15:57:30','2022-05-12 15:57:30',6,NULL),
	 ('Data dan informasi yang Anda berikan dalam pengajuan ini adalah sesuai dengan keadaan yang sebenar-benarnya.',2,'2022-05-12 15:57:30','2022-05-12 15:57:30',6,NULL),
	 ('Menyetujui bahwa Bank berwenang untuk:
INSERT INTO paylater.tbl_tnc_explain (description,id_tnc,created_date,updated_date,id_tnc_title,id_tnc_subtitle) VALUES
	 ('Anda mengetahui dan menyetujui bahwa akan membebaskan Bank dari segala tuntutan, jika Bank tidak dapat melaksanakan instruksi dari Anda, baik sebagian maupun sepenuhnya yang disebabkan oleh kejadian atau sebab yang berada di luar kendali atau kemampuan Bank, meliputi tetapi tidak terbatas pada bencana alam, peperangan, kerusuhan, kondisi perangkat keras, kegagalan sistem infrastruktur elektronik atau transmisi, gangguan daya, gangguan telekomunikasi, kegagalan sistem kliring atau hal lainnya yang ditetapkan oleh Bank Indonesia atau lembaga berwenang lainnya.',2,'2022-05-12 15:59:46','2022-05-12 15:59:46',7,NULL),
	 ('Setelah kejadian yang menyebabkan Bank tidak dapat melaksanakan instruksi dari Anda berakhir, Bank akan melanjutkan kembali instruksi tersebut dalam kurun waktu sesuai dengan ketentuan dari Bank Indonesia dan atau Otoritas Jasa Keuangan.',2,'2022-05-12 15:59:46','2022-05-12 15:59:46',7,NULL),
	 ('Anda dapat menghubungi Layanan Pelanggan untuk bertanya, mengajukan permintaan, dan/atau mengajukan pengaduan ke Nobu Call di nomor 1500278 atau e-mail di nobucall@nobubank.com. Jika Anda ingin mengajukan pengaduan secara tertulis, Anda harus menyertakan bukti yang mendukung pengaduan tersebut.',2,'2022-05-12 16:00:58','2022-05-12 16:00:58',8,NULL),
	 ('Jika pengaduan tersebut terkait dengan transaksi keuangan yang telah dilakukan, Anda harus mengajukannya paling lambat 14 (empat belas) hari kalender sejak transaksi keuangan tersebut dilakukan.',2,'2022-05-12 16:00:58','2022-05-12 16:00:58',8,NULL),
	 ('Bank akan melakukan pemeriksaan/penyelidikan atas pengaduan tersebut sesuai dengan kebijakan dan prosedur yang berlaku di Bank.',2,'2022-05-12 16:00:58','2022-05-12 16:00:58',8,NULL);